     */
    function add()
    {   
        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params = array(
				'NumberId' => $this->input->post('NumberId'),
            );
            
            $competitor_id = $this->Competitor_model->add_competitor($params);
            redirect('competitor/index');
        }
        else
        {            
            $data['_view'] = 'competitor/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a competitor
     */
    function edit($CompetitorId)
    {   
        // check if the competitor exists before trying to edit it
        $data['competitor'] = $this->Competitor_model->get_competitor($CompetitorId);
        
        if(isset($data['competitor']['CompetitorId']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'NumberId' => $this->input->post('NumberId'),
                );

                $this->Competitor_model->update_competitor($CompetitorId,$params);            
                redirect('competitor/index');
            }
            else
            {
                $data['_view'] = 'competitor/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The competitor you are trying to edit does not exist.');
    } 

