     */
    function remove($CompetitorId)
    {
        $competitor = $this->Competitor_model->get_competitor($CompetitorId);

        // check if the competitor exists before trying to delete it
        if(isset($competitor['CompetitorId']))
        {
            $this->Competitor_model->delete_competitor($CompetitorId);
